!(function () {
  const e = document,
    t = e.documentElement,
    n = e.body,
    i = e.getElementById("lights-toggle"),
    s = (window.sr = ScrollReveal());

  // Function to handle theme switching
  function a() {
    let e = i.parentNode.querySelector(".label-text");

    // Dark theme when the checkbox is checked, light theme when unchecked
    if (i.checked) {
      n.classList.add("lights-off"); // Apply dark theme
      e && (e.innerHTML = "light"); // Update label to 'light'
    } else {
      n.classList.remove("lights-off"); // Remove dark theme (light theme active)
      e && (e.innerHTML = "dark"); // Update label to 'dark'
    }
  }

  t.classList.remove("no-js");
  t.classList.add("js");

  // On page load, set the checkbox to unchecked by default (light theme)
  window.addEventListener("load", function () {
    n.classList.add("is-loaded");
    i.checked = false; // Default to light theme
    a(); // Apply the default theme
  });

  n.classList.contains("has-animations") &&
    window.addEventListener("load", function () {
      s.reveal(".feature", {
        duration: 600,
        distance: "20px",
        easing: "cubic-bezier(0.215, 0.61, 0.355, 1)",
        origin: "right",
        viewFactor: 0.2,
      });
    });

  // Attach event listener to toggle the theme when the checkbox changes
  i && i.addEventListener("change", a);
})();
